package org.tnsif.intro;

public class DataTypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a=127; // byte take 8 bits
		int b=120; // it takes 1 byte
		float c=12/5f;  // float it takes decimal number
		double d=12/5;
		double e=12d/5d;
		long i=70343256;
		char f='R';
		// ASCII Value
		int g='C';
		String s="Rutuja";

		// boolean is always in true or false
		boolean h=true;

		System.out.println("The value of a is "+a);
		System.out.println("The value of b is "+b);
		System.out.println("The value of c is "+c);
		System.out.println("The value of d is "+d);
		System.out.println("The value of e is "+e);
		System.out.println("The value of f is"+f);
		System.out.println("The value of g is"+g);
		System.out.println("The name is "+s);
		System.out.println("The bolean of h is"+h);
		System.out.println("The value of i is"+i);


	}

}
